CREATE DATABASE interview_prep;
USE interview_prep;

CREATE TABLE users (
    id INT AUTO_INCREMENT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    email VARCHAR(255) UNIQUE NOT NULL,
    password VARCHAR(255) NOT NULL
);


-- CREATE TABLE questions (
--   id INT AUTO_INCREMENT PRIMARY KEY,
--   job_role VARCHAR(100),
--   question TEXT
-- );
CREATE TABLE questions (
    id INT(11) AUTO_INCREMENT PRIMARY KEY,
    question TEXT NOT NULL,
    type VARCHAR(50) NOT NULL,            -- 'MCQ' or 'Mock'
    options TEXT,                         -- All options stored in one TEXT field (can be JSON or delimiter-separated)
    answer TEXT NOT NULL,                 -- Correct answer (e.g., 'A', 'B', etc.)
    job_role VARCHAR(100) NOT NULL,       -- e.g., 'AI Engineer', 'Web Developer'
    difficulty VARCHAR(50) NOT NULL       -- 'Easy', 'Medium', 'Hard'
);

CREATE TABLE user_results (
    id INT(11) AUTO_INCREMENT PRIMARY KEY,
    username VARCHAR(100) NOT NULL,
    total_marks INT(11) NOT NULL,
    correct_answers INT(11) NOT NULL,
    total_questions INT(11) NOT NULL,
    percentage FLOAT NOT NULL,
    test_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE sessions (
    id INT AUTO_INCREMENT PRIMARY KEY,
    transcript TEXT NOT NULL,
    wpm INT,
    filler_words INT,
    timestamp DATETIME DEFAULT CURRENT_TIMESTAMP
);



CREATE TABLE usage_history (
    id INT AUTO_INCREMENT PRIMARY KEY,
    user_id INT NOT NULL,
    activity TEXT NOT NULL,
    activity_time DATETIME DEFAULT CURRENT_TIMESTAMP
);

-- Table for uploaded resumes
CREATE TABLE resumes (
    id INT AUTO_INCREMENT PRIMARY KEY,
    file_name VARCHAR(255) NOT NULL,
    file_path VARCHAR(255) NOT NULL,
    file_size INT NOT NULL,
    file_type VARCHAR(50) NOT NULL,
    uploaded_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

-- Table for resume analysis results
CREATE TABLE resume_analysis (
    id INT AUTO_INCREMENT PRIMARY KEY,
    resume_id INT NOT NULL,
    sections_found TEXT,
    score INT,
    suggestions TEXT,
    analyzed_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (resume_id) REFERENCES resumes(id) ON DELETE CASCADE
);

-- Table for built resumes
CREATE TABLE built_resumes (
    id INT AUTO_INCREMENT PRIMARY KEY,
    full_name VARCHAR(100),
    email VARCHAR(100),
    phone VARCHAR(20),
    education TEXT,
    experience TEXT,
    skills TEXT,
    projects TEXT,
    certifications TEXT,
    template_used VARCHAR(50),
    generated_file_path VARCHAR(255),
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);



